driverCodes.javascript:
const fs = require('fs');

// Định nghĩa một class rỗng để tránh lỗi ReferenceError nếu người dùng không dùng class
class Solution {}

// __USER_CODE_HERE__

try {
    const input = fs.readFileSync(0, 'utf8').split('\n');
    if (input.length < 2) process.exit(0);

    const s = input[0].trim();
    const p = input[1].trim();

    const sol = new Solution();
    // Kiểm tra xem hàm strStr nằm ở global (var/function) hay nằm trong class Solution
    const fn = (typeof strStr === 'function') ? strStr : (sol.strStr ? sol.strStr.bind(sol) : null);

    if (typeof fn === 'function') {
        const res = fn(s, p);
        process.stdout.write(res.toString());
    } else {
        console.error("Error: function strStr not found.");
    }
} catch (e) {
    console.error(e);
}

