class Solution {
public:
    int jump(vector<int>& nums) {
        if (nums.size() == 1) return 0;
        int maxReach = nums[0];
        int step = nums[0];
        int jumps = 1;
        for (int i = 1; i < nums.size(); i++) {
            if (i == nums.size() - 1) return jumps;
            maxReach = max(maxReach, i + nums[i]);
            step--;
            if (step == 0) {
                jumps++;
                if (i >= maxReach) return -1;
                step = maxReach - i;
            }
        }
        return -1;
    }
};

