code.python:
class Solution:
    def reverse(self, x: int) -> int:
        sign = -1 if x < 0 else 1
        x *= sign
        res = 0
        while x > 0:
            res = res * 10 + x % 10
            x //= 10
        if res > 2**31 - 1: return 0
        return sign * res

code.javascript:
/**
 * @param {number} x
 * @return {number}
 */
var reverse = function(x) {
    const max = Math.pow(2, 31) - 1;
    const min = -Math.pow(2, 31);
    let res = 0;
    let sign = x < 0 ? -1 : 1;
    x = Math.abs(x);
    while (x) {
        res = res * 10 + x % 10;
        x = Math.floor(x / 10);
        if (res > max || res < min) return 0;
    }
    return sign * res;
};

code.java:
public class Solution {
    public int reverse(int x) {
        long res = 0;
        while (x != 0) {
            res = res * 10 + x % 10;
            x /= 10;
            if (res > Integer.MAX_VALUE || res < Integer.MIN_VALUE) return 0;
        }
        return (int) res;
    }
}
